import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecipeList = () => {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        // Fetch the recipes from the backend API
        axios.get('/api/recipes')
            .then(response => {
                setRecipes(response.data);
            })
            .catch(error => {
                console.error('Error fetching recipes:', error);
            });
    }, []);

    return (
        <div className="recipe-list">
            <h1>Recipe Dashboard</h1>
            {recipes.length > 0 ? (
                recipes.map((recipe) => (
                    <div key={recipe.id} className="recipe-card">
                        <h2>{recipe.name}</h2>
                        <p><strong>Created Date:</strong> {recipe.createdDate}</p>
                        <p><strong>Last Updated At:</strong> {recipe.lastUpdatedAt}</p>
                        <p><strong>Type:</strong> {recipe.type ? recipe.type : 'N/A'}</p>
                        <p><strong>Servings:</strong> {recipe.servings}</p>
                        <p><strong>Vegetarian:</strong> {recipe.isVegetarian ? 'Yes' : 'No'}</p>
                        <p><strong>Ingredients:</strong></p>
                        <ul>
                            {recipe.ingredients.map((ingredient, index) => (
                                <li key={index}>{ingredient}</li>
                            ))}
                        </ul>
                        <p><strong>Instructions:</strong> {recipe.instructions}</p>
                    </div>
                ))
            ) : (
                <p>No recipes found.</p>
            )}
        </div>
    );
};

export default RecipeList;
